import './App.css';
import io from 'socket.io-client';

export const socket = io();

function App() {
  const [thisUser, updateUser] = useState('');
  const inputUser = useRef('');

  function onButtonClick() {
    if(inputUser.current.value !== '') {
      const user = inputUser.current.value;
      updateUser( user )
    }
  }

  function logout() {
    if(thisUser !== '') {
      updateUser('');
    }
  }

    if(thisUser === '') {


      return (
        <div className='wrapper-input'>
          <div>
          <input type="text" ref={inputUser} placeholder="username" required />
          <button type="button" onClick={onButtonClick}><h3>Log In</h3></button>
          </div>
        </div>
      );


    }
    else{
      return (
        <div className='wrapper-input'>
        <div>
        <h1>Hello {thisUser}</h1>
        <p1> Let's start investing </p1>
        </div>
        <div>
        <button onClick = {logout}> Log Out </button>
        </div>
        </div>
        )
    }



}

export default App;
