      return (
        <div>
          <input type="text" ref={inputUser} placeholder="username" required />
          <button type="button" onClick={onButtonClick}>Log In</button>
        </div>
      );


    }
    else{
      return (
        <>
        <h1>Username: {thisUser}</h1>
        <button onClick = {logout}> Log Out </button>

        </>
        )
    }



}

export default App;
